import React from "react";
import leftImg from "../img/item-4.png";
import item1 from "../img/item-1.png";
import item2 from "../img/item-2.png";
import item3 from "../img/item-9.png";

class Details extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      productsNum: 0,
    };
  }

  increasePro = () => {
    this.setState({ productsNum: this.state.productsNum + 1 });
  };
  discreasePro = () => {
    this.setState({ productsNum: this.state.productsNum - 1 });
  };

  render() {
    const { description, name, price } = this.props.products[1];
    const { productsNumber } = this.props;
    return (
      <>
        <section className="product-info">
          <div className="left-photos">
            <img alt="img" src={leftImg} />
            <div className="img-container left">
              <img alt="img" src={leftImg} />
            </div>
            <div className="img-container right">
              <img alt="img" src={leftImg} />
            </div>
          </div>

          <div className="main-photo">
            <img alt="img" src={leftImg} />
          </div>

          <div className="product-details">
            <h1>{price}</h1>
            <h2>{name}</h2>
            <p>{description}</p>
            <div className="color-box">
              <p>
                <strong>Color:</strong> Pink
              </p>
              <span className="color"></span>
              <span className="color"></span>
              <span className="color"></span>
            </div>

            <div className="add-box">
              <div className="number-box">
                <span className="discrease" onClick={this.discreasePro}>
                  -
                </span>
                <span className="count"> {productsNumber} </span>
                <span className="increase" onClick={this.increasePro}>
                  +
                </span>
              </div>
              <span className="add-btn" onClick={this.props.addFun}>
                ADD TO CART
              </span>
            </div>
          </div>
        </section>

        <h1 className="title">Might also like</h1>

        <section className="suggested-products">
          <div className="product-item">
            <img alt="img" src={item1} />
            <div className="i-caption">
              <p>Speradsheets shirt</p>
              <p>$25</p>
            </div>
          </div>

          <div className="product-item">
            <img alt="img" src={item2} />
            <div className="i-caption">
              <p>Speradsheets shirt</p>
              <p>$25</p>
            </div>
          </div>

          <div className="product-item">
            <img alt="img" src={item3} />
            <div className="i-caption">
              <p>Speradsheets shirt</p>
              <p>$25</p>
            </div>
          </div>
        </section>
        <span className="more-btn">MORE PRODUCTS</span>
      </>
    );
  }
}

export default Details;
